module mainSim(

    );
//    wire [3999 : 0] bigSeq = 4000';
    wire [3999 : 0] bigSeq = 4000';
    wire [79 : 0] smallSeq = 80'b00010001010000101000000100100001100000011000100010000001000100011000001000101000;
    reg [11 : 0] startLoc = 3999;
    wire [11 : 0] position;
    reg started = 1'b0;
    reg clk = 1'b0;
    reg start = 1'b1;
    reg rst = 1'b0;
    reg setUp = 1'b0;
    wire done, found;
    offTargetSearcher Searcher
    (
        .CLK(clk),
        .RST(rst),
        .START(start),
        .bigSeq(bigSeq),
        .startIndex(startLoc),
        .smallSeq(smallSeq),
        .location(position),
        .DONE(done),
        .found(found)
    );
    
    always
        #1 clk = ~clk;
    
    always @ (posedge clk)
    begin
        if (~setUp)
        begin
            start <= 1'b1;
            rst <= 1'b0;
            setUp <= 1'b1;
            wait (100);
        end
        else if (~started)
        begin
            start <= 1'b0;
            rst <= 1'b1;
            started <= 1'b1;
        end
        else
            start <= 1'b1;
    end
    
endmodule
